namespace BinaryStringEvaluator.Tests
{
    public class UnitTest1
    {
        [Theory]
        [InlineData("", false)]
        [InlineData("121100", false)]
        [InlineData("1212", false)]
        [InlineData("11100", false)]
        [InlineData("000000000", false)]
        [InlineData("1010101010101010", true)]
        [InlineData("0101010101010101", false)]
        [InlineData("11111111110000000000", true)]
        [InlineData("101010101001010101010101010", false)]
        [InlineData("101010101001010101010101010101010101001010101010101010101010101001010101010101010101010101001010101010101010101010101001010101010101010", false)]
        [InlineData("", false)]
        [InlineData("", false)]
        public void Test1(string value, bool expected)
        {
            var result = BinaryStringEvaluator.Evaluate(value);
            Assert.Equal(expected, result);
        }
    }
}